import pandas as pdimport matplotlib.pyplot as pltimport resong_df = pd.read_csv('song_data.csv')columns = list(song_df.columns)# print(columns)columns.remove('song_name')columns.remove('audio_mode')columns.remove('time_signature')columns.remove('key')# print(columns)for col in columns:    columns[columns.index(col)] = [col, list(song_df[col])]    # print(columns)for i in range(len(columns)):    for j in range(len(columns[i][1])):        columns[i][1][j] = abs(columns[i][1][j])    columns[i][1] = max(columns[i][1])    # print(columns)for col in columns:    song_df[col[0]] = song_df[col[0]] / col[1]        lists = dict()    for i in range(len(columns)):    lists[columns[i][0]] = list(song_df[columns[i][0]]).sort(reverse=True)    # print(lists)songs = dict()for i in range(len(song_df['song_name'])):    songs[song_df['song_name'][i]] = song_df.loc[i, 'song_popularity':].tolist()# print(songs)all_columns = list(song_df.columns)all_columns.pop(0)def song_chart(name):    plt.bar(all_columns, songs[name])    plt.ylabel(name)    plt.xticks(rotation='vertical')    plt.show()    # song_chart('Last Nite')while True:    s = input("\nEnter a song name (q to quit): ")        if s != 'q':        song = s.title().strip()        r = re.compile(".*[" + song[1:-2] + ".*")        l = list(filter(r.match, list(song_df['song_name'])))        print(l)                if len(l) != 0:            print("{} chart created.".format(l[0]))            song_chart(l[0])            continue        else:            print("Song not found.")            continue        elif s == 'q':        break    